import React from 'react';
import Grid from '@material-ui/core/Grid';
import '../styles/Home.css';
import aakanksha_pic from '../resources/aakanksha_pic.png'
import ReactTextRotator from 'react-text-rotator';
var ReactRotatingText = require('react-rotating-text');



const content = [
    {
        text: ' Aakanksha N S',
        animation: 'fade'
    },
    {
        text: ' a Data Scientist',
        animation: 'fade',
    },
    {
        text: ' a Software Engineer',
        animation: 'fade',
    },
    {
        text: ' a Web Developer',
        animation: 'fade',
    }
];

const Home = () => (
    <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
            <div>
                <img src={aakanksha_pic} alt="Home pic" className="my-pic" />
            </div>
        </Grid>
        <Grid item xs={12} sm={6}>
            <Grid container spacing={0}>
                <Grid item xs={12}>
                    <div className='about-text'>
                        I am
                        <ReactRotatingText items={[' Aakanksha NS', ' a Data Scientist', ' a Software Engineer', ' a Web Developer']} />
                        <ReactTextRotator
                            content={content}
                            time={3000}
                            startDelay={100}
                        />
                    </div>
                </Grid>
                <Grid>
                    <div className='description'>
                        Hi, I'm Aakanksha, a graduate student pursuing my Masters in Data Science at <a href="https://www.usfca.edu/" target="_blank">University of San Francisco</a>.
                        <br></br>
                        <br></br>
                        I worked as a software developer at JP Morgan Chase for two years after
                        graduating from National Institute of Technology, Calicut.
                        I enjoy data science, competitive coding and web development. Check out my
                        projects to know more about my work.
                    </div>
                </Grid>
            </Grid>
        </Grid>
    </Grid>
);

export default Home;
