import React, { Component } from 'react';
import './App.css';
import { BrowserRouter as Router, Route } from "react-router-dom";
import { Navbar, Home } from './components';

export const ROUTES = {
  'ABOUT': 1,
  'CONTACT': 2,
  'PROJECTS': 3,
  'RESUME': 4
};

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      route: ROUTES.ABOUT
    }
  }

  render() {
    return (
      <div className="App">
        <Router>
          <div className="header">
            <Navbar route={route} />
          </div>
          <div className="main-content">
            <Route path="/" exact render={() => {
              this.updateRoute(ROUTES.ABOUT);
              return <About />;
            }} />
            <Route path="/projects/" render={() => {
              this.updateRoute(ROUTES.PROJECTS);
              return <Projects />;
            }} />
            <Route path="/resume/" render={() => {
              this.updateRoute(ROUTES.RESUME);
              return <Resume />;
            }} />
            <Route path="/contact/" render={() => {
              this.updateRoute(ROUTES.CONTACT);
              return <Contact />;
            }} />
            <Route path="/seo/" render={() => {
              this.updateRoute(ROUTES.PROJECTS);
              return <SEO />;
            }} />
            <Route path="/project-desc/" render={(state) => {
              console.log(state);
              this.updateRoute(ROUTES.PROJECTS);
              return <ProjectDescription projectData={state.location.state.projectData} />;
            }} />
          </div>
        </Router>
      </div>
    );
  }
}

export default App;
